              type="submit"
              disabled={loading}
              className="flex h-12 w-full items-center justify-center gap-2 rounded-2xl bg-primary text-sm font-semibold text-primary-foreground transition-all hover:opacity-90 disabled:opacity-50 m3-elevation-1"
            >
              {loading ? (
                <div className="h-5 w-5 animate-spin rounded-full border-2 border-primary-foreground/30 border-t-primary-foreground" />
              ) : (
                <>
                  Ajouter l'adresse
                  <Plus className="h-4 w-4" />
                </>
              )}
            </button>
          </form>
        </div>
      </main>
    </div>
  );
}

import { useState } from "react";
import { useAuth } from "@/contexts/AuthContext";
import { MapPin, Mail, Lock, ArrowRight, UserPlus } from "lucide-react";
import { toast } from "sonner";

export default function AuthPage() {
  const { login, register } = useAuth();
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    try {
      if (isLogin) {
        await login(email, password);
        toast.success("Connexion russie !");
      } else {
        await register(email, password);
        toast.success("Compte cr avec succs !");
      }
    } catch (err: any) {
      toast.error(err.message || "Une erreur est survenue");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex min-h-screen items-center justify-center bg-background p-4">
      <div className="w-full max-w-md animate-slide-up">
        {/* Logo */}
        <div className="mb-8 text-center">
          <div className="mx-auto mb-4 flex items-center justify-center">
            <img src="/logo.svg" alt="My Favorite Addresses Logo" className="h-16 w-16" />
          </div>
          <h1 className="text-2xl font-bold text-foreground">
            My Favorite Addresses
          </h1>
          <p className="mt-1 text-sm text-muted-foreground">
            Sauvegardez et retrouvez vos adresses prfres
          </p>
        </div>

        {/* Card */}
        <div className="m3-card p-6 sm:p-8">
          {/* Toggle */}
          <div className="mb-6 flex rounded-2xl bg-secondary p-1">
            <button
              onClick={() => setIsLogin(true)}
              className={`flex-1 rounded-xl py-2.5 text-sm font-medium transition-all ${isLogin
                  ? "bg-card text-foreground m3-elevation-1"
                  : "text-muted-foreground hover:text-foreground"
                }`}
            >
              Connexion
            </button>
            <button
              onClick={() => setIsLogin(false)}
              className={`flex-1 rounded-xl py-2.5 text-sm font-medium transition-all ${!isLogin
                  ? "bg-card text-foreground m3-elevation-1"
                  : "text-muted-foreground hover:text-foreground"
                }`}
            >
              Inscription
            </button>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="mb-1.5 block text-sm font-medium text-foreground">
                Email
              </label>
              <div className="relative">
                <Mail className="absolute left-3.5 top-1/2 h-4 w-4 -translate-y-1/2 text-muted-foreground" />
                <input
                  type="email"
                  required
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="votre@email.com"
                  className="h-12 w-full rounded-2xl border border-border bg-surface-container pl-10 pr-4 text-sm text-foreground placeholder:text-muted-foreground outline-none transition-all focus:border-primary focus:ring-2 focus:ring-primary/20"
                />
              </div>
            </div>

            <div>
              <label className="mb-1.5 block text-sm font-medium text-foreground">
                Mot de passe
              </label>
              <div className="relative">
                <Lock className="absolute left-3.5 top-1/2 h-4 w-4 -translate-y-1/2 text-muted-foreground" />
                <input
                  type="password"
                  required
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder=""
                  className="h-12 w-full rounded-2xl border border-border bg-surface-container pl-10 pr-4 text-sm text-foreground placeholder:text-muted-foreground outline-none transition-all focus:border-primary focus:ring-2 focus:ring-primary/20"
                />
              </div>
            </div>

            <button
              type="submit"
              disabled={loading}
              className="flex h-12 w-full items-center justify-center gap-2 rounded-2xl bg-primary text-sm font-semibold text-primary-foreground transition-all hover:opacity-90 disabled:opacity-50 m3-elevation-1"
            >
              {loading ? (
                <div className="h-5 w-5 animate-spin rounded-full border-2 border-primary-foreground/30 border-t-primary-foreground" />
              ) : isLogin ? (
                <>
                  Se connecter
                  <ArrowRight className="h-4 w-4" />
                </>
              ) : (
                <>
                  Crer un compte
                  <UserPlus className="h-4 w-4" />
                </>
              )}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

import { useState, useEffect, useCallback } from "react";
import { getAddresses, updateAddress, deleteAddress, type Address } from "@/services/api";
import { AddressCard } from "@/components/AddressCard";
import { AppHeader } from "@/components/AppHeader";
import { Plus, Search, MapPin, Compass, Trash2 } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { toast } from "sonner";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";

export default function Dashboard() {
  const [addresses, setAddresses] = useState<Address[]>([]);
  const [loading, setLoading] = useState(true);

  // Dialog states
  const [addressToEdit, setAddressToEdit] = useState<Address | null>(null);
  const [addressToDelete, setAddressToDelete] = useState<Address | null>(null);

  // Edit form states
  const [editName, setEditName] = useState("");
  const [editDescription, setEditDescription] = useState("");
  const [isUpdating, setIsUpdating] = useState(false);
  const [isDeleting, setIsDeleting] = useState(false);

  const navigate = useNavigate();

  const fetchAddresses = useCallback(async () => {
    try {
      const { items } = await getAddresses();
      setAddresses(items);
    } catch (err: any) {
      toast.error("Impossible de charger les adresses");
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchAddresses();
  }, [fetchAddresses]);

  const handleEditClick = (address: Address) => {
    setAddressToEdit(address);
    setEditName(address.name);
    setEditDescription(address.description || "");
  };

  const handleDeleteClick = (address: Address) => {
    setAddressToDelete(address);
  };

  const handleUpdateSubmit = async () => {
    if (!addressToEdit || !editName.trim()) return;

    setIsUpdating(true);
    try {
      const { item } = await updateAddress(addressToEdit.id, {
        name: editName,
        description: editDescription,
      });

      setAddresses((prev) =>
        prev.map((addr) => addr.id === item.id ? item : addr)
      );

      toast.success("Adresse modifie avec succs");
      setAddressToEdit(null);
    } catch (err) {
      toast.error("chec de la modification de l'adresse");
    } finally {
      setIsUpdating(false);
    }
  };

  const handleConfirmDelete = async () => {
    if (!addressToDelete) return;

    setIsDeleting(true);
    try {
      await deleteAddress(addressToDelete.id);
      setAddresses((prev) => prev.filter((addr) => addr.id !== addressToDelete.id));
      toast.success("Adresse supprime");
      setAddressToDelete(null);
    } catch (err) {
      toast.error("chec de la suppression de l'adresse");
    } finally {
      setIsDeleting(false);
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <AppHeader />

      <main className="mx-auto max-w-5xl px-4 py-6 sm:px-6 sm:py-8">
        {/* Actions */}
        <div className="mb-6 flex flex-col gap-3 sm:flex-row sm:items-center sm:justify-between">
          <div>
            <h2 className="text-xl font-bold text-foreground">Mes Adresses</h2>
            <p className="text-sm text-muted-foreground">
              {addresses.length} adresse{addresses.length !== 1 ? "s" : ""} sauvegarde{addresses.length !== 1 ? "s" : ""}
            </p>
          </div>
          <div className="flex gap-2">
            <button
              onClick={() => navigate("/search")}
              className="flex h-10 items-center gap-2 rounded-2xl border border-border bg-card px-4 text-sm font-medium text-foreground transition-all hover:bg-secondary m3-elevation-1"
            >
              <Compass className="h-4 w-4" />
              Proximit
            </button>
            <button
              onClick={() => navigate("/add")}
              className="flex h-10 items-center gap-2 rounded-2xl bg-primary px-4 text-sm font-semibold text-primary-foreground transition-all hover:opacity-90 m3-elevation-1"
            >
              <Plus className="h-4 w-4" />
              Ajouter
            </button>
          </div>
        </div>

        {/* Content */}
        {loading ? (
          <div className="grid gap-4 sm:grid-cols-2">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="m3-card h-28 animate-pulse p-5">
                <div className="flex gap-4">
                  <div className="h-11 w-11 rounded-2xl bg-secondary" />
                  <div className="flex-1 space-y-2">
                    <div className="h-4 w-2/3 rounded-lg bg-secondary" />
                    <div className="h-3 w-full rounded-lg bg-secondary" />
                    <div className="h-3 w-1/3 rounded-lg bg-secondary" />
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : addresses.length === 0 ? (
          <div className="m3-card flex flex-col items-center justify-center py-16 text-center animate-fade-in">
            <div className="mb-4 flex h-16 w-16 items-center justify-center rounded-3xl bg-secondary">
              <MapPin className="h-8 w-8 text-primary" />
            </div>
            <h3 className="text-lg font-semibold text-foreground">
              Aucune adresse
            </h3>
            <p className="mt-1 text-sm text-muted-foreground">
              Commencez par ajouter votre premire adresse favorite
            </p>
            <button
              onClick={() => navigate("/add")}
              className="mt-5 flex h-10 items-center gap-2 rounded-2xl bg-primary px-5 text-sm font-semibold text-primary-foreground transition-all hover:opacity-90"
            >
              <Plus className="h-4 w-4" />
              Ajouter une adresse
            </button>
          </div>
        ) : (
          <div className="grid gap-4 sm:grid-cols-2">
            {addresses.map((addr, i) => (
              <AddressCard
                key={addr.id}
                address={addr}
                index={i}
                onEdit={handleEditClick}
                onDelete={handleDeleteClick}
              />
            ))}
          </div>
        )}
      </main>

      {/* Edit Address Dialog */}
      <Dialog open={!!addressToEdit} onOpenChange={(open) => !open && setAddressToEdit(null)}>
        <DialogContent className="sm:max-w-[425px]">
          <DialogHeader>
            <DialogTitle>Modifier l'adresse</DialogTitle>
            <DialogDescription>
              Faites des changements pour personnaliser cette adresse favorite.
            </DialogDescription>
          </DialogHeader>
          <div className="grid gap-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="name">Nom</Label>
              <Input
                id="name"
                value={editName}
                onChange={(e) => setEditName(e.target.value)}
                placeholder="Ex: Mon bureau"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="description">Description (Optionnel)</Label>
              <Textarea
                id="description"
                value={editDescription}
                onChange={(e) => setEditDescription(e.target.value)}
                placeholder="Dtails supplmentaires..."
                rows={3}
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setAddressToEdit(null)}>
              Annuler
            </Button>
            <Button onClick={handleUpdateSubmit} disabled={isUpdating || !editName.trim()}>
              {isUpdating ? "Enregistrement..." : "Enregistrer"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Delete Confirmation Dialog */}
      <Dialog open={!!addressToDelete} onOpenChange={(open) => !open && setAddressToDelete(null)}>
        <DialogContent className="sm:max-w-[425px]">
          <DialogHeader>
            <DialogTitle className="text-destructive flex flex-row items-center gap-2">
              <Trash2 className="h-5 w-5" />
              Supprimer l'adresse ?
            </DialogTitle>
            <DialogDescription>
              tes-vous sr de vouloir supprimer "{addressToDelete?.name}" ? Cette action est irrversible.
            </DialogDescription>
          </DialogHeader>
          <DialogFooter className="mt-4 flex gap-2 sm:justify-end">
            <Button variant="outline" onClick={() => setAddressToDelete(null)}>
              Annuler
            </Button>
            <Button variant="destructive" onClick={handleConfirmDelete} disabled={isDeleting}>
              {isDeleting ? "Suppression..." : "Supprimer"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

import { useState } from "react";
import { searchAddresses, type Address } from "@/services/api";
import { AddressCard } from "@/components/AddressCard";
import { AppHeader } from "@/components/AppHeader";
import { ArrowLeft, Compass, Crosshair, Search } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { toast } from "sonner";

export default function SearchPage() {
  const navigate = useNavigate();
  const [radius, setRadius] = useState("10");
  const [lat, setLat] = useState("");
  const [lng, setLng] = useState("");
  const [results, setResults] = useState<Address[] | null>(null);
  const [loading, setLoading] = useState(false);

  const useMyLocation = () => {
    if (!navigator.geolocation) {
      toast.error("La golocalisation n'est pas supporte");
      return;
    }
    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setLat(pos.coords.latitude.toString());
        setLng(pos.coords.longitude.toString());
        toast.success("Position rcupre !");
      },
      () => toast.error("Impossible de rcuprer votre position")
    );
  };

  const handleSearch = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    try {
      const { items } = await searchAddresses({
        radius: parseFloat(radius),
        from: { lat: parseFloat(lat), lng: parseFloat(lng) },
      });
      setResults(items);
      if (items.length === 0) toast.info("Aucune adresse trouve dans ce rayon");
    } catch (err: any) {
      toast.error(err.message || "Erreur lors de la recherche");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <AppHeader />
      <main className="mx-auto max-w-5xl px-4 py-6 sm:px-6 sm:py-8">
        <button
          onClick={() => navigate("/")}
          className="mb-4 flex items-center gap-1.5 text-sm text-muted-foreground hover:text-foreground transition-colors"
        >
          <ArrowLeft className="h-4 w-4" />
          Retour
        </button>

        <div className="m3-card p-6 sm:p-8 animate-slide-up max-w-lg">
          <div className="mb-6 flex items-center gap-3">
            <div className="flex h-11 w-11 items-center justify-center rounded-2xl bg-accent text-accent-foreground">
              <Compass className="h-5 w-5" />
            </div>
            <div>
              <h2 className="text-lg font-bold text-foreground">
                Recherche de proximit
              </h2>
              <p className="text-sm text-muted-foreground">
                Trouvez les adresses autour d'un point
              </p>
            </div>
          </div>

          <form onSubmit={handleSearch} className="space-y-4">
            <div className="grid grid-cols-2 gap-3">
              <div>
                <label className="mb-1.5 block text-sm font-medium text-foreground">
                  Latitude
                </label>
                <input
                  type="number"
                  step="any"
                  required
                  value={lat}
                  onChange={(e) => setLat(e.target.value)}
                  placeholder="48.8566"
                  className="h-12 w-full rounded-2xl border border-border bg-surface-container px-4 text-sm text-foreground placeholder:text-muted-foreground outline-none transition-all focus:border-primary focus:ring-2 focus:ring-primary/20"
                />
              </div>
              <div>
                <label className="mb-1.5 block text-sm font-medium text-foreground">
                  Longitude
                </label>
                <input
                  type="number"
                  step="any"
                  required
                  value={lng}
                  onChange={(e) => setLng(e.target.value)}
                  placeholder="2.3522"
                  className="h-12 w-full rounded-2xl border border-border bg-surface-container px-4 text-sm text-foreground placeholder:text-muted-foreground outline-none transition-all focus:border-primary focus:ring-2 focus:ring-primary/20"
                />
              </div>
            </div>

            <button
              type="button"
              onClick={useMyLocation}
              className="flex h-10 items-center gap-2 rounded-2xl border border-border bg-card px-4 text-sm font-medium text-foreground transition-all hover:bg-secondary"
            >
              <Crosshair className="h-4 w-4 text-accent" />
              Utiliser ma position
            </button>

            <div>
              <label className="mb-1.5 block text-sm font-medium text-foreground">
                Rayon (km)
              </label>
              <input
                type="number"
                required
                min="1"
                value={radius}
                onChange={(e) => setRadius(e.target.value)}
                className="h-12 w-full rounded-2xl border border-border bg-surface-container px-4 text-sm text-foreground placeholder:text-muted-foreground outline-none transition-all focus:border-primary focus:ring-2 focus:ring-primary/20"
              />
            </div>

            <button
              type="submit"
              disabled={loading}
              className="flex h-12 w-full items-center justify-center gap-2 rounded-2xl bg-primary text-sm font-semibold text-primary-foreground transition-all hover:opacity-90 disabled:opacity-50 m3-elevation-1"
            >
              {loading ? (
                <div className="h-5 w-5 animate-spin rounded-full border-2 border-primary-foreground/30 border-t-primary-foreground" />
              ) : (
                <>
                  Rechercher
                  <Search className="h-4 w-4" />
                </>
              )}
            </button>
          </form>
        </div>

        {results !== null && (
          <div className="mt-8">
            <h3 className="mb-4 text-lg font-semibold text-foreground">
              Rsultats ({results.length})
            </h3>
            {results.length > 0 ? (
              <div className="grid gap-4 sm:grid-cols-2">
                {results.map((addr, i) => (
                  <AddressCard key={addr.id} address={addr} index={i} />
                ))}
              </div>
            ) : (
              <div className="m3-card flex items-center justify-center py-12 text-center">
                <p className="text-sm text-muted-foreground">
                  Aucune adresse trouve dans ce rayon
                </p>
              </div>
            )}
          </div>
        )}
      </main>
    </div>
  );
}

